                    const video = document.querySelector('.js-video-' + id);
                    const videos = document.querySelectorAll('.js-video');

                    if (this.muted) {

                        videos.forEach(video => {
                            video.pause();
                            video.muted = true;
                        });

                        video.play();

                        video.muted = false;
                        this.muted = false;

                        this.$dispatch('component-cursor', {
                            icon: 'volume-on'
                        });

                    } else {

                        video.muted = true;
                        this.muted = true;

                        this.$dispatch('component-cursor', {
                            icon: 'volume-off'
                        });

                    }


